deviceEventHandlers.deviceEventHandler:


import com.qmatic.qp.api.device.DeviceCommand

// remove any old flash timer
timerStopDisplayFlash = device.getState("TIMER_STOP_DISPLAY_FLASH")
if(timerStopDisplayFlash) {
  device.getResource("EVENT_SCHEDULER").remove(timerStopDisplayFlash)
}

DeviceCommand displayCommand = new DeviceCommand(device.getId(), "OUTPUT_LED")
displayCommand.setParameters(event.getParameters())
displayCommand.setParameter("displayEnabled", false)
device.executeCommand(displayCommand)

deviceCommandHandlers.deviceCommandHandler:


import com.qmatic.qp.api.device.DeviceCommand
import com.qmatic.qp.api.device.DeviceEvent

// remove any old flash timer
timerStopDisplayFlash = device.getState("TIMER_STOP_DISPLAY_FLASH")
if(timerStopDisplayFlash) {
  device.getResource("EVENT_SCHEDULER").remove(timerStopDisplayFlash)
}

DeviceCommand displayCommand = new DeviceCommand(device.getId(), "OUTPUT_LED")
displayCommand.setParameters(command.getParameters())
device.executeCommand(displayCommand)

// restart timer to flash display
if(command.getParameter("displayFlash")) {
  DeviceEvent eDisplayFlash = new DeviceEvent(device.getId(), "STOP_FLASH")
  eDisplayFlash.setParameters(command.getParameters())
  timerStopDisplayFlash = device.getResource("EVENT_SCHEDULER").scheduleEvent(device.getUnit().getConfiguration().get("flashTime"), eDisplayFlash)
  device.setState("TIMER_STOP_DISPLAY_FLASH", timerStopDisplayFlash)
}

// restart timer to turn off display
if(device.getUnit().getConfiguration().get("timeLEDOff") > 0) {
  DeviceCommand cmdDisplayOffTimerReset = new DeviceCommand(device.getId(), "DISPLAY_OFF_TIMER_RESET")
  device.executeCommand(cmdDisplayOffTimerReset)
}

import com.qmatic.qp.api.device.DeviceEvent import com.qmatic.qp.api.device.DeviceCommand
displayEnabled = command.getParameter("displayEnabled") displayFlash = command.getParameter("displayFlash") servicePointFlash = device.getUnit().getConfiguration().get("flashServicePointId")
// set default display script to turn off display displayScript = " D- "
if(displayEnabled) {
servicePointLogicId = device.getUnit().getLogicId()
device.setState("TICKET", "")
switch(device.getState("USER_STATE")) { case "SERVING": case "CALLED":
String ticketId = command.getParameter("ticket") String displayText = "" String displayNUM = ticketId String displaySP = servicePointLogicId.toString()
device.setState("TICKET", ticketId)
switch(device.getUnit().getConfiguration().get("showOnCall")) { case "NUM": // show ticket id if not null if ( ticketId != null ) { String ticketIdLetter= String.format("%1\$1.1s", ticketId) // check if ticketId is too large to fit on a D924 if( ticketId.size() > 4) { if(ticketIdLetter.isNumber()) { displayNUM = String.format("%1\$4.4s", ticketId.substring(0)) } else { displayNUM = String.format("%1\$4.4s", ticketId.substring(1)) } } displayText = (displayFlash ? "" + (char)27 + "f+" : "") + displayNUM } break case "SP": // show service point id displayText = (displayFlash ? "" + (char)27 + "f+" : "") + displaySP break case "SPNUM": // show service point id and ticket id displayText = (displayFlash && servicePointFlash ? "" + (char)27 + "f+" : "") + displaySP + " " + (char)27 + "f" + (displayFlash ? "+" : "-") + displayNUM break case "NUMSP": // show service point id and ticket id displayText = (displayFlash ? "" + (char)27 + "f+" : "") + displayNUM + " " + (char)27 + "f" + (displayFlash && servicePointFlash ? "+" : "-") + displaySP break } displayScript = (command.getParameter("bell") ? " B" : "") + " PC I " + displayText break case "IN_STORE_NEXT": txt = device.getUnit().getConfiguration().get("textWaiting") txt = txt.replaceAll("\\{servicePointId\\}", servicePointLogicId.toString()) txtscroll = device.getUnit().getConfiguration().get("textWaitingScroll") txtspeed = device.getUnit().getConfiguration().get("textWaitingScrollspeed") displayScript = "F- " + txtscroll + " SMS " + txtspeed + " I [" + txt + "]" break case "INACTIVE": case "NO_CALLABLE_VISITS": txt = device.getUnit().getConfiguration().get("textOpen") txt = txt.replaceAll("\\{servicePointId\\}", servicePointLogicId.toString()) txtscroll = device.getUnit().getConfiguration().get("textOpenScroll") txtspeed = device.getUnit().getConfiguration().get("textOpenScrollspeed") displayScript = "F- " + txtscroll + " SMS " + txtspeed + " I [" + txt + "]" break default: txt = device.getUnit().getConfiguration().get("textClosed") txt = txt.replaceAll("\\{servicePointId\\}", servicePointLogicId.toString()) txtscroll = device.getUnit().getConfiguration().get("textClosedScroll") txtspeed = device.getUnit().getConfiguration().get("textClosedScrollspeed") displayScript = "F- " + txtscroll + " SMS " + txtspeed + " I [" + txt + "]" break }
}
displayCommand = [] displayCommand.add([displayScript]) toDisplay(displayCommand)
def toDisplay(s) { def output = s as Object[][] DeviceEvent displayEvent = new DeviceEvent(device.getId(),"DISPLAY") displayEvent.setParameter("output",output) result = true try { result = device.sendEvent(displayEvent) } catch(e) { device.log("ERROR", e.getMessage()) result = false } }

