  receiveChatEvent.setUnitId(wsp.getId())
  receiveChatEvent.setEventName("CHAT_RECEIVE")
  wsp.sendEvent(receiveChatEvent)
} catch (Throwable t) {
  wsp.log("WARN", "Unable to send CHAT_RECEIVE event to web terminal, error:" + t.getMessage())
}
