  wsp = unit.getDevice("Web service point")
  DeviceEvent de = new DeviceEvent()
  de.setUnitId(wsp.getId())
  de.setEventName(event.getEventName())
  de.setParameters(event.getParameters())
  try {
    wsp.sendEvent(de)
  } catch(e) {
    //  not ok
  }


